
tinymce.PluginManager.add('iyibukod', function(editor, url) {

    // Add a button that opens a window
    editor.addButton('iyibukod', {
        icon: true,
		image: url+'/img/iyibu.png',
		tooltip: 'iyibu!Kodu Ekle',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Web Kodu Ekle',
				body: [
                    {type: 'textbox', name: 'title', value: editor.selection.getContent({format : 'text'}),
					multiline: true,
					style: 'width: 650px; height: 300px;'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent(e.data.title);
                },
            });
        }
    });
tinymce.init({
    setup: function(editor) {
        editor.on('ObjectSelected', function(e) {
            console.log('ObjectSelected event', e);
        });
    }
});
});